surveyCss:
#_pi_surveyWidget {
  z-index: 1;
  border: 0px;
  border-radius: 0px;
  background-image: linear-gradient(
    rgba(255, 255, 255, 0.9), 
    rgba(200, 200, 200,0.9));
  background-origin: padding-box;  
  -webkit-background-clip: border-box;
  -webkit-background-origin: padding-box;
  -webkit-background-size: auto;
  background-attachment: scroll;
  background-clip: border-box;
  background-color: rgba(0, 0, 0, 0);
  margin: 0px;
}



/* ------ 1.1 - Combined Question and Answers Box --------*/
._pi_widgetContentContainer {
  max-width: 100%;
  margin-left: auto;
  margin-right: auto;
  margin-top: 15px;
  margin-bottom: 30px;
  /background-color:orange;
}


/* ------ 1.2 - Question Box -----------------------------*/
._pi_question {
  width: 90%;
  color: #000;
  text-align: center;
  font-size: 110%;
  font-weight: normal;
  margin: auto;
  padding: auto;
  /background-color: yellow;
}

/* ------ 1.3 - All Answers Box --------------------------*/
ul._pi_answers_container {
  max-width: 50%;
  min-width: 50%;

  margin-top: 0px;
  margin-bottom: 0px;
  margin-right: auto;
  margin-left: auto; 
  
  padding-top: 10px;
  padding-bottom: 0px;
  padding-left: 0px;
  padding-right: 0px;
  
  /background-color: green;
}

ul._pi_answers_container:before {
  content: "Not likely";
  color: rgba(0,0,0,0.6);
  font-size: 90%;

  padding-top: 13px;
  position: absolute;
  display: block;
  margin-left: -80px;

  /background-color: purple;
}

ul._pi_answers_container:after {
  content: "Very likely";
  color: rgba(0,0,0,0.6);
  font-size: 90%;

  padding-top: 13px;
  padding-left: 10px;
  position: absolute;
  
  /background-color: purple;
}

/* --------------------- 1.3.1 - Individual Answer Box & Hover --------------*/
ul._pi_answers_container li {
  border: 0px;
  border-radius:  0%;
  background-color: rgba(0,0,0,0.2);

  width: 09.65%;
  height: 45px;

  margin-top: 0px;
  margin-bottom: 0px;
  margin-right: 3%;
  margin-left: 4%;  

  padding-top: 1%;
  padding-bottom: 1%;
  padding-left: 0px;
  padding-right: 0px;
}

ul._pi_answers_container li:hover {
  border: 0px;
  border-radius: 0%;
  background-color: rgba(0,0,0,0.7);
} 


/* --------------------- 1.3.2 - Individual Answer Placement in Box ----------*/
ul._pi_answers_container li a {
  margin: 0px;
  padding: 0px;
}



/* --------------------- 1.3.3 - Individual Answer Text & Hover -------------*/
ul._pi_answers_container li a label {
  font-weight: normal;
  font-size: 120%;
  color: #000;
  text-align: center;
  border-radius: 50%;
  
  width: 70%;
  height: 50%;
  position: absolute;
  top: 5%;


  margin-top: 0%;
  margin-bottom: 0px;
  margin-left: 15%;
  margin-right: 0%;
  
  padding-top: 0%;
  padding-bottom: 0px;
  padding-left: 0px;
  padding-right: 0px;

  /background-color: purple;
}

ul._pi_answers_container li a label:hover {
  color: #fff;
  text-decoration: none;
}


/* --------------------- 1.3.4 - Radio Button Outer & Hover -----------------------*/
ul._pi_answers_container li a span._pi_radio_button_outer {
  border: 0px solid #48b8d2;
  width: 0px;
  height: 0px;  
  top: 0px;
}

ul._pi_answers_container li:hover a span._pi_radio_button_outer {
  border: 0px solid purple;
  width: 0px;
  height: 0px;  
  top: 0px;
}

/* --------------------- 1.3.5 - Radio Button Inner & Hover ---------------*/
ul._pi_answers_container li a span._pi_radio_button_inner {
  border: 0px solid #48b8d2;
  width: 0px;
  height: 0px;  
  top: 0px;
  border-radius: 0px;
}

ul._pi_answers_container li:hover a span._pi_radio_button_inner {
  border: 0px solid #48b8d2;
  width: 0px;
  height: 0px;  
  top: 0px;
  border-radius: 0px;
}


/* ------ 1.4 - Close Button & Hover -------------------*/
._pi_closeButton {
  /font-family:verdana;
  font-size: 120%;
  font-weight: normal;
  position:absolute;
  top: 7px;
  right: 10px;
  color: rgba(0,0,0,0.6);
}

._pi_closeButton:hover {
  font-size: 120%;
  top: 7px;
  right: 10px;
  color: rgba(0,0,0,1);
}


/* ------ 1.5 - PI Branding Text and Hover -------------*/
a._pi_branding {
  font-weight: normal;
  color: rgba(0,0,0,0.6);
  margin-right: auto;
  margin-left: auto;
  width: 100%;
  font-size: 65%;
  text-align: center;
  text-decoration:none;
  padding: 0px;
  bottom: 4%;
  left: 0%;
  border: 0px solid #fff;
  /background-color: purple;
}

a._pi_branding:hover {
  font-weight: normal;
  font-size: 65%;
  color: rgba(0,0,0,1);
  text-decoration: none;
  bottom: 4%;
  left: 0%;
}


/* ------ 1.6 - Invitation Text Box -------------*/
._pi_invitationTextContainer {
  font-size: 1px;
  font-weight: 400;
  color: #fff;
  margin: 0px;
}


/* ------ 1.7 - Start Button & Hover -------------*/
._pi_startButton {
  font-size: 1px;
  font-weight: 400;
  padding: 0px;
  margin: 0px
  background-color: #fff;
  color: #fff
}

._pi_startButton:hover {
  text-decoration:none
}

/* ------ 1.8 - Thank You & Loading -------------*/
._pi_loadingSurvey,._pi_thankYouSurvey {
  color: #000;
  font-size: 20px;
  font-weight: normal;
  margin-top: 50px;
  margin-bottom: 50px;
  text-align: center;
}

