  padding-bottom: 0px !important;
}

._pi_question_custom_content_question p {
  color: #000;
  font-size: 16px;
  text-align: center;
  margin-bottom: 1rem;
}

#_pi_surveyWidgetContainer.mobile-enabled ._pi_question_custom_content_question p {
  font-size: 14px;
}

._pi_question_custom_content_question p:last-child {
  margin-bottom: 0 !important;
}

/* ANSWERS: Single Choice */

/* BUTTONS: Free Text Submit, Multi Choice Submit, Custom Content */

input._pi_free_text_question_submit_button,
