/* ================== DEFAULT | SURVEY WIDGET ================== */

/*  SURVEY WIDGET */

div#_pi_surveyWidget {
  height: auto;
  width: 100%;
  margin: auto;
  padding-bottom: 0px;
  display: -ms-flexbox;
  display: flex;
  -ms-flex-direction: column;
  flex-direction: column;
  -ms-flex-wrap: nowrap;
  flex-wrap: nowrap;
  -ms-flex-pack: start;
  justify-content: flex-start;
  -ms-flex-align: center;
  align-items: center;
  /* 	    background-color: PeachPuff !important;   */
}

div#_pi_surveyWidgetCustom {
  display: -ms-flexbox;
  display: flex;
  -ms-flex-direction: column;
  flex-direction: column;
  -ms-flex-wrap: nowrap;
  flex-wrap: nowrap;
  -ms-flex-pack: justify;
  justify-content: space-between;
  -ms-flex-align: center;
  align-items: center;
  -ms-flex-line-pack: justify;
  align-content: space-between;
  /* 		background-color: pink !important; */
}

/* ================== DEFAULT | CLOSE BUTTON ================== */

/* 	A) CLOSE BUTTON */

div._pi_closeButton,
div._pi_closeCustomButton {
  position: static !important;
}

div#_pi_surveyWidgetCustom ._pi_closeCustomButton {
  /* 		background-color: red !important; */
}

/* 	A) CLOSE BUTTON | BOTTOM BAR */

/* ================== DEFAULT | CONTENT CONTAINER ================== */

/* 	B) CONTENT CONTAINER */

#_pi_surveyWidget div._pi_widgetContentContainer,
#_pi_surveyWidgetCustom div._pi_widgetContentContainer {
  display: -ms-flexbox;
  display: flex;
  -ms-flex-direction: row;
  flex-direction: row;
  -ms-flex-wrap: wrap;
  flex-wrap: wrap;
  -ms-flex-pack: center;
  justify-content: center;
  -ms-flex-align: center;
  align-items: center;
  height: auto;
  width: 100%;
  margin: auto !important;
  padding: 0;
  /* 	    background-color: aquamarine !important; */
}

div#_pi_surveyWidgetCustom ._pi_widgetContentContainer {
  background-color: #fff;
  padding: 15px;
}

/* 	B) CONTENT CONTAINER | desktop */

@media (min-width: 769px) {
  #_pi_surveyWidget div._pi_widgetContentContainer,
  #_pi_surveyWidgetCustom div._pi_widgetContentContainer {
    max-width: 900px;
  }
}

