  padding-right: 15px;
  /* Match it to single-choice label right & left padding */
}

/* Hidden Checkbox Form Element (nothing) */

/* Simulated Checkbox */

._pi-control-indicator {
  position: static;
  display: block;
  -ms-flex: 0 0 14px;
  flex: 0 0 14px;
  margin-top: 2px;
  /* CREATE a variable for vertical adjust of the box */
  margin-bottom: 0;
