  margin-right: 10px;
  /* Make sure it matches the variable for radio left margin */
  width: 14px;
  height: 14px;
  border: 1px solid #000;
}

/* Simulated Checkmark (nothing) */

._pi-control-checkbox ._pi-control-indicator:after {
  left: 4px;
  width: 4px;
  height: 10px;
  border: solid #000;
  border-width: 0 2px 2px 0;
  transform: rotate(45deg);
}

